import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { MessageSquare, Send, X } from 'lucide-react';
import { chatWithGemini } from '../utils/api';

const Chatbot = ({ onClose }) => {
  const [messages, setMessages] = useState([
    { id: 1, text: '¬°Hola! Soy tu asistente de FitLife powered by Gemini AI. ¬øEn qu√© puedo ayudarte hoy?', sender: 'bot' }
  ]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (input.trim() === '') return;

    const newMessage = { id: Date.now(), text: input, sender: 'user' };
    setMessages((prevMessages) => [...prevMessages, newMessage]);
    const userInput = input;
    setInput('');

    // Show typing indicator
    const typingMessage = { 
      id: Date.now() + 1, 
      text: 'Gemini est√° pensando...', 
      sender: 'bot',
      isTyping: true 
    };
    setMessages((prevMessages) => [...prevMessages, typingMessage]);

    try {
      console.log('üöÄ Enviando mensaje a Gemini:', userInput);

      // Call Gemini API - ONLY use AI response
      const botResponse = await chatWithGemini(userInput);

      console.log('‚úÖ Respuesta recibida de Gemini');

      // Remove typing indicator and add real AI response
      setMessages((prevMessages) => {
        const withoutTyping = prevMessages.filter(msg => !msg.isTyping);
        return [...withoutTyping, { 
          id: Date.now() + 2, 
          text: botResponse, 
          sender: 'bot' 
        }];
      });

    } catch (error) {
      console.error('‚ùå Error al conectar con Gemini:', error);

      // Remove typing indicator and show connection error
      setMessages((prevMessages) => {
        const withoutTyping = prevMessages.filter(msg => !msg.isTyping);
        return [...withoutTyping, { 
          id: Date.now() + 2, 
          text: `‚ùå Error de conexi√≥n con Gemini AI: ${error.message}. Por favor, verifica tu conexi√≥n a internet y tu API key.`, 
          sender: 'bot' 
        }];
      });
    }
  };

  return (
    <motion.div
      className="fixed bottom-4 right-4 w-80 h-[400px] bg-white/95 backdrop-blur-xl border border-gray-200/50 rounded-3xl shadow-2xl flex flex-col z-50 dark:bg-gray-800/95 dark:border-gray-700"
      initial={{ opacity: 0, y: 50 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: 50 }}
      transition={{ duration: 0.3 }}
    >
      <div className="flex items-center justify-between p-4 border-b border-gray-200 dark:border-gray-700">
        <div className="flex items-center gap-2">
          <MessageSquare className="w-5 h-5 text-blue-600 dark:text-blue-400" />
          <h3 className="font-bold text-gray-900 dark:text-white">Asistente FitLife</h3>
        </div>
        <motion.button
          onClick={onClose}
          className="p-1 rounded-full hover:bg-gray-100 dark:hover:bg-gray-700 text-gray-600 dark:text-gray-400"
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          <X className="w-4 h-4" />
        </motion.button>
      </div>

      <div className="flex-1 overflow-y-auto p-4 space-y-4 custom-scrollbar">
        <AnimatePresence initial={false}>
          {messages.map((msg) => (
            <motion.div
              key={msg.id}
              className={`flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.2 }}
            >
              <div
                className={`max-w-[75%] p-3 rounded-2xl ${
                  msg.sender === 'user'
                    ? 'bg-blue-500 text-white rounded-br-none'
                    : 'bg-gray-100 text-gray-800 rounded-bl-none dark:bg-gray-700 dark:text-gray-200'
                }`}
              >
                {msg.isTyping ? (
                  <div className="flex items-center gap-1">
                    <span>{msg.text}</span>
                    <div className="flex gap-1">
                      <div className="w-1 h-1 bg-gray-400 rounded-full animate-bounce"></div>
                      <div className="w-1 h-1 bg-gray-400 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
                      <div className="w-1 h-1 bg-gray-400 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
                    </div>
                  </div>
                ) : (
                  msg.text
                )}
              </div>
            </motion.div>
          ))}
        </AnimatePresence>
        <div ref={messagesEndRef} />
      </div>

      <form onSubmit={handleSendMessage} className="p-4 border-t border-gray-200 dark:border-gray-700 flex items-center gap-2">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Escribe tu mensaje..."
          className="flex-1 px-4 py-2 bg-gray-50 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500/30 dark:bg-gray-700 dark:border-gray-600 dark:text-white"
        />
        <motion.button
          type="submit"
          className="p-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition-colors"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <Send className="w-5 h-5" />
        </motion.button>
      </form>
    </motion.div>
  );
};

export default Chatbot;
